            }
          })
          ;
          setMovies(movieList);
          console.log('movieList',movieList);
      })
      .catch(error => console.log('error:',error));
  }*/

  return (
    <React.Fragment>
      <section>
        <button onClick={fetchMovies}>Fetch Movies</button>
      </section>
      <section>
      <MoviesList moviess={movies} />
      </section>
    </React.Fragment>
  );
}

export default App;
