import React,{useState} from 'react';

import MoviesList from './components/MoviesList';
import './App.css';

function App() {
   const [movies,setMovies] = useState('');
   const [isLoading,setIsloading] = useState(false);
   const [error,setError] = useState(null);
  const fetchMovies = () => {
      console.log("fetching movies...");
      setIsloading(true);
      fetch('https://swapi.dev/api/films/')
      .then(response => {
        //console.log('Response received: ',response.json())
      return response.json();
      })
      .then(responsejson => {
          const movieList = responsejson.results.map(movie => {
            return {
              id: movie.episode_id,
              title: movie.title,
              openingtext: movie.opening_crawl,
              releaseDate:movie.release_date
            }
          });
          setIsloading(false);
          setMovies(movieList);
          console.log('movieList',movieList);
      })
      .catch(error => {
        console.log('error:',error)
        setError(error);
      });
  }

  return (
    <React.Fragment>
      <section>
        <button onClick={fetchMovies}>Fetch Movies</button>
      </section>
      <section>
      {!isLoading && movies.length!=0 && error!=null && <MoviesList moviess={movies} />}
      {isLoading && <p>Loading...</p>}
      {!isLoading && movies.length===0  && error!=null && <p>No Movies...</p>}
      </section>
    </React.Fragment>
  );
}

export default App;
